sqlMap.select:
/*select read list Feedback*/
SELECT a.id,a.id, a.userId, a.articleId, ''  FROM feedback a WHERE 1=1
 and a.ID = #id# and a.USERID = #userId# and a.ARTICLEID = #articleId# LIMIT #query_start#,#query_number#

/*select read Feedback*/
SELECT a.id,a.id, a.userId, a.articleId, ''  FROM feedback a WHERE 1=1 
 and a.ID = #id# and a.USERID = #userId# and a.ARTICLEID = #articleId#

sqlMap.insert:
/* insert Feedback*/
INSERT INTO feedback(userId, articleId) 
VALUES (#userId#, #articleId#)

sqlMap.update:
/* update Feedback*/
update feedback SET ID = #id#
,ID = #id#,USERID = #userId#,ARTICLEID = #articleId# WHERE ID = #id#

sqlMap.delete:
/*delete Feedback*/
delete FROM feedback WHERE 1=1
 and ID = #id# and USERID = #userId# and ARTICLEID = #articleId#

sqlMap.select:
		/*count Feedback*/
		SELECT count(*) FROM feedback a WHERE 1=1 

 and a.ID = #id# and a.USERID = #userId# and a.ARTICLEID = #articleId#
