sqlMap.select:
/*select read list Hobby*/ SELECT a.id, a.title, a.categoryId FROM hobby a WHERE 1=1 and a.ID = #id#
and a.TITLE = #title# and a.CATEGORYID = #categoryId#
LIMIT #query_start#,#query_number#

sqlMap.update:
/* update Hobby*/ update hobby SET ID = #id# ,ID = #id#
,TITLE = #title# ,CATEGORYID = #categoryId#
WHERE ID = #id#

sqlMap.select:
/*count Hobby*/ SELECT count(*) FROM hobby a WHERE 1=1
and a.ID = #id#
and a.TITLE = #title# and a.CATEGORYID = #categoryId#
